import React, { useState } from 'react';
import './App.css'; // Import app.css here
import HomeFeed from './pages/HomeFeed';
import PostDetails from './pages/PostDetails'; // Import PostDetails component
import CreatePostForm from './pages/CreatePostForm';
import EditPostForm from './pages/EditPost'; // Import EditPostForm component
import { BrowserRouter as Router, Route } from 'react-router-dom';

const App = () => {
    const [selectedPost, setSelectedPost] = useState(null);

    // Function to handle selecting a post in HomeFeed
    const handlePostSelected = (post) => {
        setSelectedPost(post);
    };

    return (
        <div className="App">
            <Router>
                <div className="header">
                    <h1>Let's hear what you think about NFL teams</h1>
                    <button onClick={() => window.location.href = '/'}>View Posts üåü</button>
                    <button onClick={() => window.location.href = '/new'}>Add New Post üõ†Ô∏è</button>
                    <a href="/about"><button>About ‚ÑπÔ∏è</button></a>
                </div>
                <Route exact path="/" component={() => <HomeFeed onPostSelected={handlePostSelected} />} />
                <Route exact path="/new" component={CreatePostForm} />
                <Route exact path="/edit-post/:postId" component={EditPostForm} />
                <Route exact path="/post" component={() => <PostDetails post={selectedPost} />} />
            </Router>
        </div>
    );
}

export default App;
