import React, { useState, useEffect } from 'react';
import './HomeFeed.css'; // Import the CSS file
import supabase from '../client'; // Assuming you have a Supabase client instance set up

const HomeFeed = ({ onPostSelected }) => {
    const [posts, setPosts] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        // Fetch posts data from Supabase when the component mounts
        const fetchPosts = async () => {
            try {
                const { data, error } = await supabase.from('Posts').select('*');
                if (error) {
                    throw error;
                }
                setPosts(data);
                setLoading(false);
            } catch (error) {
                console.error('Error fetching posts:', error.message);
            }
        };

        fetchPosts();
    }, []);

    const handleUpvote = async (postId) => {
        try {
            const { data, error } = await supabase
                .from('Posts')
                .update({ upvotes: supabase.raw('upvotes + 1') })
                .eq('id', postId);
            if (error) {
                throw error;
            }
            // Update the local state with the updated post data
            const updatedPosts = posts.map(post => {
                if (post.id === postId) {
                    return { ...post, upvotes: post.upvotes + 1 };
                }
                return post;
            });
            setPosts(updatedPosts);
        } catch (error) {
            console.error('Error adding upvote:', error.message);
        }
    };

    if (loading) {
        return <div>Loading...</div>; // Display loading state while fetching data
    }

    return (
        <div>
            <h2>Home Feed</h2>
            <ul>
                {posts.map((post) => (
                    <li key={post.id} className="post">
                        <p>Title: {post.title}</p>
                        <p>Content: {post.content}</p>
                        <p>UserID: {post.username}</p>
                        <p>Upvotes: {post.upvotes}</p>
                        {post.imageUrl && <img src={post.imageUrl} alt="Post Image" />}
                        <p className="created-at">Created At: {post.created_at}</p>
                        <button onClick={() => onPostSelected(post)}>View Details</button>
                        <button onClick={() => handleUpvote(post.id)}>Upvote</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default HomeFeed;
