import React, { useState, useEffect } from 'react';
import './PostDetails.css'; // Import the CSS file
import supabase from '../client'; // Assuming you have a Supabase client instance set up

const PostDetails = ({ post }) => {
    const [comments, setComments] = useState([]);
    const [loading, setLoading] = useState(true);
    const [newComment, setNewComment] = useState('');
    const [submitting, setSubmitting] = useState(false);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchComments = async () => {
            try {
                // Fetch comments associated with the current post
                const { data, error } = await supabase
                    .from('Posts')
                    .select('comments')
                    .eq('id', post.id)
                    .single();
                if (error) throw error;
                // Ensure comments are always initialized as an array
                setComments(data.comments ? JSON.parse(data.comments) : []);
                setLoading(false);
            } catch (error) {
                console.error('Error fetching comments:', error.message);
                setError('Error fetching comments. Please try again later.');
                setLoading(false);
            }
        };

        fetchComments();
    }, [post]);

    const handleSubmitComment = async (e) => {
        e.preventDefault();
        if (!newComment.trim()) return;

        setSubmitting(true);

        try {
            // Update the comments field in the database to add the new comment
            const { data, error } = await supabase
                .from('Posts')
                .update({
                    comments: JSON.stringify([...comments, { content: newComment, username: 'Anonymous' }]) // Add new comment object
                })
                .eq('id', post.id);
            if (error) throw error;

            // Update the local state with the new comment
            setComments([...comments, { content: newComment, username: 'Anonymous' }]);
            setNewComment('');
        } catch (error) {
            console.error('Error adding comment:', error.message);
        } finally {
            setSubmitting(false);
        }
    };

    if (loading) {
        return <div>Loading comments...</div>;
    }

    if (error) {
        return <div>Error: {error}</div>;
    }

    return (
        <div>
            <h2>Post Details</h2>
            <p>Title: {post.title}</p>
            <p>Content: {post.content}</p>
            <p>User Name: {post.username}</p>
            <p>Upvotes: {post.upvotes}</p>
            {post.imageUrl && <img src={post.imageUrl} alt="Post Image" />}
            <p>Created At: {post.created_at}</p>
            <h3>Comments</h3>
            {comments.length > 0 ? (
                <ul>
                    {comments.map((comment, index) => (
                        <li key={index}>
                            <p>User: {comment.username}</p>
                            <p>Content: {comment.content}</p>
                        </li>
                    ))}
                </ul>
            ) : (
                <p>No comments available.</p>
            )}
            <form onSubmit={handleSubmitComment}>
                <textarea
                    value={newComment}
                    onChange={(e) => setNewComment(e.target.value)}
                    placeholder="Write your comment here..."
                    disabled={submitting}
                />
                <button type="submit" disabled={!newComment.trim() || submitting}>
                    {submitting ? 'Submitting...' : 'Submit Comment'}
                </button>
            </form>
        </div>
    );
};

export default PostDetails;
