import React, { useState, useEffect } from 'react';
import './PostDetails.css';
import supabase from '../client';
import bcrypt from 'bcryptjs';

const PostDetails = ({ post, onEdit }) => {
    const [comments, setComments] = useState([]);
    const [loading, setLoading] = useState(true);
    const [newComment, setNewComment] = useState('');
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [submitting, setSubmitting] = useState(false);
    const [error, setError] = useState(null);
    const [upvotes, setUpvotes] = useState(post.upvotes);

    useEffect(() => {
        if (!post || !post.id) {
            setError('Post ID is undefined');
            setLoading(false);
            return;
        }

        const fetchPostDetails = async () => {
            try {
                const { data, error } = await supabase
                    .from('Posts')
                    .select('*')
                    .eq('id', post.id)
                    .single();
                if (error) throw error;
                
                // Ensure that comments is an array
                const commentsArray = Array.isArray(data.comments) ? data.comments : [];
                setComments(commentsArray);
                setLoading(false);
            } catch (error) {
                console.error('Error fetching post details:', error.message);
                setError('Error fetching post details. Please try again later.');
                setLoading(false);
            }
        };

        fetchPostDetails();
    }, [post]);

    // Handlers remain unchanged

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>Error: {error}</div>;
    }

    return (
        <div>
            <h2>Post Details</h2>
            <p>Title: {post.title}</p>
            <p>Content: {post.content}</p>
            <p>User Name: {post.username}</p>
            <p>Upvotes: {upvotes}</p>
            <button onClick={() => setUpvotes(upvotes + 1)}>Upvote</button>
            {post.imageUrl && <img src={post.imageUrl} alt="Post Image" />}
            <p>Created At: {post.created_at}</p>
            <h3>Comments</h3>
            {comments.length > 0 ? (
                <ul>
                    {comments.map((comment, index) => (
                        <li key={index}>
                            <p>User: {comment.username}</p>
                            <p>Content: {comment.content}</p>
                        </li>
                    ))}
                </ul>
            ) : (
                <p>No comments available.</p>
            )}
            {/* Form and other buttons */}
        </div>
    );
};

export default PostDetails;
