import React, { useState, useEffect } from 'react';
import './PostDetails.css'; // Import the CSS file
import supabase from '../client'; // Assuming you have a Supabase client instance set up

const PostDetails = ({ post }) => {
    const [comments, setComments] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        // Fetch comments data from Supabase when the component mounts
        const fetchComments = async () => {
            try {
                const { data, error } = await supabase
                    .from('Posts')
                    .select('*')
                    .eq('postId', post.id);
                if (error) {
                    throw error;
                }
                setComments(data);
                setLoading(false);
            } catch (error) {
                console.error('Error fetching comments:', error.message);
            }
        };

        fetchComments();
    }, [post.id]); // Fetch comments whenever the post id changes

    if (loading) {
        return <div>Loading comments...</div>; // Display loading state while fetching data
    }

    return (
        <div>
            <h2>Post Details</h2>
            <p>Title: {post.title}</p>
            <p>Content: {post.content}</p>
            <p>User Name: {post.username}</p>
            <p>Upvotes: {post.upvotes}</p>
            {post.imageUrl && <img src={post.imageUrl} alt="Post Image" />}
            <p>Created At: {post.created_at}</p>
            <h3>Comments</h3>
            <ul>
                {comments.map((comment) => (
                    <li key={comment.id}>
                        <p>{comment.content}</p>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default PostDetails;
