import { Link } from 'react-router-dom';
import './ReadCrewmates.css'; // In ReadCrewmates.js
import supabase from '../client'; // Import your Supabase client
import React, { useEffect, useState } from 'react';



const ReadCrewmates = () => {
  // Define state to hold crewmates data
  const [crewmates, setCrewmates] = useState([]);

  // Fetch crewmates data from Supabase on component mount
  useEffect(() => {
      const fetchData = async () => {
          try {
              // Fetch crewmates data from Supabase
              const { data, error } = await supabase.from('Posts').select('*');
              if (error) {
                  throw error;
              }

              // Update state with fetched data
              setCrewmates(data || []);
          } catch (error) {
              console.error('Error fetching crewmates:', error.message);
          }
      };

      fetchData();
  }, []);

  return (
      <div>
          <h2>Crewmate List</h2>
          {crewmates.length > 0 ? (
              <ul>
                  {crewmates.map((crewmate) => (
                      <li key={crewmate.id}>
                          <h3>{crewmate.name}</h3>
                          <p>MPH: {crewmate.mph}</p>
                          <p>Color: {crewmate.color}</p>
                          {/* Display category based on crewmate's category */}
                          <p>Category: {crewmate.category}</p>
                          <Link to={`/edit/${crewmate.id}`}>Edit</Link>
                      </li>
                  ))}
              </ul>
          ) : (
              <p>No crewmates found. Add some!</p>
          )}
      </div>
  );
};

// Export the ReadCrewmates component
export default ReadCrewmates;
