import React, { useState, useEffect } from 'react';
import './HomeFeed.css'; // Import the CSS file
import supabase from '../client'; // Assuming you have a Supabase client instance set up

const HomeFeed = ({ onPostSelected }) => {
    const [posts, setPosts] = useState([]);
    const [loading, setLoading] = useState(true);
    const [sortBy, setSortBy] = useState('created_at'); // Default sorting criteria

    useEffect(() => {
        // Fetch posts data from Supabase when the component mounts
        const fetchPosts = async () => {
            try {
                let { data, error } = await supabase.from('Posts').select('*').order(sortBy, { ascending: false });
                if (error) {
                    throw error;
                }
                setPosts(data);
                setLoading(false);
            } catch (error) {
                console.error('Error fetching posts:', error.message);
            }
        };

        fetchPosts();
    }, [sortBy]); // Update posts when sortBy changes

    const handleUpvote = async (postId, post) => {
        try {
            const { data, error } = await supabase
                .from('Posts')
                .update({ upvotes: post.upvotes + 1 })
                .eq('id', postId);
            if (error) {
                throw error;
            }
            // Update the local state with the updated post data
            const updatedPosts = posts.map(p => {
                if (p.id === postId) {
                    return { ...p, upvotes: p.upvotes + 1 };
                }
                return p;
            });
            setPosts(updatedPosts);
        } catch (error) {
            console.error('Error adding upvote:', error.message);
        }
    };

    const handleSortChange = (criteria) => {
        setSortBy(criteria);
    };

    if (loading) {
        return <div>Loading...</div>; // Display loading state while fetching data
    }

    // Function to handle clicking on a post
    const handlePostClick = (post) => {
        onPostSelected(post);
    };

    // Function to handle clicking on the "View Details" button
    const handleViewDetails = (post) => {
        // Pass the post object to the parent component for further processing
        onPostSelected(post);
    };

    return (
        <div>
            <h2>Home Feed</h2>
            <div className="sort-options">
                <label>Sort By:</label>
                <select value={sortBy} onChange={(e) => handleSortChange(e.target.value)}>
                    <option value="created_at">Created Time</option>
                    <option value="upvotes">Upvotes Count</option>
                </select>
            </div>
            <ul>
                {posts.map((post) => (
                    <li key={post.id} className="post">
                        <p>Title: {post.title}</p>
                        <p>Content: {post.content}</p>
                        <p>UserID: {post.username}</p>
                        <p>Upvotes: {post.upvotes}</p>
                        {post.imageUrl && <img src={post.imageUrl} alt="Post Image" />}
                        <p className="created-at">Created At: {post.created_at}</p>
                        <button onClick={() => handleUpvote(post.id, post)}>Upvote</button>
                        <button onClick={() => handleViewDetails(post)}>View Details</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default HomeFeed;
