import React, { useState } from 'react';
import './CreatePostForm.css'; // Import the CSS file
import supabase from '../client'; // Import the Supabase client
import bcrypt from 'bcryptjs'; // Import bcrypt to hash the password

const CreatePostForm = ({ onPostCreated }) => {
    const [title, setTitle] = useState('');
    const [content, setContent] = useState('');
    const [imageUrl, setImageUrl] = useState('');
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState(''); // State for password

    // Generate a random user ID when the component mounts
    useState(() => {
        const randomUserId = Math.random().toString(36).substring(2);
        setUsername(randomUserId);
    }, []);

    const handleSubmit = async (e) => {
        e.preventDefault();
        // Hash the password before sending it to the database
        const salt = bcrypt.genSaltSync(10);
        const hashedPassword = bcrypt.hashSync(password, salt);

        try {
            const newPost = {
                title,
                content,
                imageUrl,
                username,
                created_at: new Date().toISOString(),
                upvotes: 0,
                password_hash: hashedPassword, // Store the hashed password
            };

            const { data, error } = await supabase.from('Posts').insert([newPost]);
            if (error) {
                throw error;
            }
            // Pass the newly created post data to the parent component
            onPostCreated(data[0]);

            // Clear the form fields after submission
            setTitle('');
            setContent('');
            setImageUrl('');
            setUsername('');
            setPassword(''); // Clear the password field
        } catch (error) {
            console.error('Error inserting new post:', error.message);
        }
    };

    return (
        <div>
            <h2>Create New Post</h2>
            <form onSubmit={handleSubmit}>
                <label>
                    Title:
                    <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} />
                </label>
                <label>
                    Content:
                    <textarea value={content} onChange={(e) => setContent(e.target.value)} />
                </label>
                <label>
                    Image URL:
                    <input type="text" value={imageUrl} onChange={(e) => setImageUrl(e.target.value)} />
                </label>
                <label>
                    Username:
                    <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} disabled />
                    {/* Disable username input to prevent changes */}
                </label>
                <label>
                    Password (for editing this post):
                    <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
                </label>
                <button type="submit">Submit</button>
            </form>
        </div>
    );
};

export default CreatePostForm;
