import React, { useState, useEffect } from 'react';
import { useHistory } from 'react-router-dom'; // Import useHistory hook for navigation
import supabase from '../client'; // Assuming you have a Supabase client instance set up
import './HomeFeed.css'; // Import the CSS file

const HomeFeed = () => {
    const history = useHistory();
    const [posts, setPosts] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        // Fetch posts data from Supabase when the component mounts
        const fetchPosts = async () => {
            try {
                const { data, error } = await supabase.from('Posts').select('*');
                if (error) {
                    throw error;
                }
                setPosts(data);
                setLoading(false);
            } catch (error) {
                console.error('Error fetching posts:', error.message);
            }
        };

        fetchPosts();
    }, []);

    const handlePostClick = (postId) => {
        history.push(`/post/${postId}`); // Navigate to post details page
    };

    if (loading) {
        return <div>Loading...</div>; // Display loading state while fetching data
    }

    return (
        <div>
            <h2>Home Feed</h2>
            <ul>
                {posts.map((post) => (
                    <li key={post.id} className="post" onClick={() => handlePostClick(post.id)}>
                        <p>Title: {post.title}</p>
                        <p>Username: {post.username}</p>
                        {post.imageUrl && <img src={post.imageUrl} alt="Post Image" />}
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default HomeFeed;
