import React, { useState, useEffect } from 'react';
import './App.css'; // Import app.css here
import HomeFeed from './pages/HomeFeed';
import PostDetails from './pages/PostDetails';
import CreatePostForm from './pages/CreatePostForm';

const App = () => {
    const [route, setRoute] = useState(window.location.pathname);

    // Listen for changes to the URL and update the route state accordingly
    useEffect(() => {
        const handleRouteChange = () => {
            setRoute(window.location.pathname);
        };
        window.addEventListener('popstate', handleRouteChange);
        return () => {
            window.removeEventListener('popstate', handleRouteChange);
        };
    }, []);

    // Define the onSubmit function to handle form submission
    const handleSubmit = async (postData) => {
        try {
            const response = await fetch('your-server-endpoint', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(postData)
            });
            if (!response.ok) {
                throw new Error('Failed to submit post');
            }
            // Handle success, e.g., refresh posts or redirect to home feed
        } catch (error) {
            console.error('Error submitting post:', error.message);
            // Handle error, e.g., display error message to the user
        }
    };

    // Render different components based on the current route
    const renderComponent = () => {
        switch (route) {
            case '/':
                return <HomeFeed />;
            case '/new':
                // Pass the onSubmit function as a prop to the CreatePostForm component
                return <CreatePostForm onSubmit={handleSubmit} />;
            case '/post/:postId':
                // You might need additional logic here to extract postId from the URL
                return <PostDetails />;
            default:
                return <div>404 Not Found</div>;
        }
    };

    return (
        <div className="App">
            <div className="header">
                <h1>üöÄ Crewmates 1.0</h1>
                <button onClick={() => setRoute('/')}>View Posts üåü</button>
                <button onClick={() => setRoute('/new')}>Add New Post üõ†Ô∏è</button>
                {/* You would typically use anchor tags for navigation in non-SPA applications */}
                <a href="/about"><button>About ‚ÑπÔ∏è</button></a>
            </div>
            {renderComponent()}
        </div>
    );
}

export default App;
