import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import './PostDetails.css';
import supabase from '../client';
import EditPost from './EditPost'; // Import the EditPost component

const PostDetails = () => {
    const [post, setPost] = useState(null);
    const [comments, setComments] = useState([]);
    const [loading, setLoading] = useState(true);
    const [newComment, setNewComment] = useState('');
    const [username, setUsername] = useState('');
    const [submitting, setSubmitting] = useState(false);
    const [error, setError] = useState(null);
    const navigate = useNavigate();
    const { postId } = useParams();

    useEffect(() => {
        const fetchPostDetails = async () => {
            try {
                const { data, error } = await supabase
                    .from('Posts')
                    .select('*')
                    .eq('id', postId)
                    .single();
                if (error) throw error;
                setPost(data);
                setLoading(false);
            } catch (error) {
                console.error('Error fetching post details:', error.message);
                setError('Error fetching post details. Please try again later.');
                setLoading(false);
            }
        };

        const fetchComments = async () => {
            try {
                const { data, error } = await supabase
                    .from('Posts')
                    .select('comments')
                    .eq('id', postId)
                    .single();
                if (error) throw error;
                setComments(data.comments ? JSON.parse(data.comments) : []);
            } catch (error) {
                console.error('Error fetching comments:', error.message);
                setError('Error fetching comments. Please try again later.');
            }
        };

        fetchPostDetails();
        fetchComments();
    }, [postId]);

    const handleEditPost = () => {
        navigate(`/edit-post/${postId}`);
    };

    const handleDeletePost = async () => {
        const confirmDelete = window.confirm('Are you sure you want to delete this post?');
        if (!confirmDelete) return;

        try {
            const { error } = await supabase.from('Posts').delete().eq('id', postId);
            if (error) throw error;
            navigate('/');
        } catch (error) {
            console.error('Error deleting post:', error.message);
        }
    };

    const handleSubmitComment = async (e) => {
        e.preventDefault();
        if (!newComment.trim()) return;

        setSubmitting(true);

        try {
            const { data, error } = await supabase
                .from('Posts')
                .update({
                    comments: JSON.stringify([...comments, { content: newComment, username }])
                })
                .eq('id', postId);
            if (error) throw error;

            setComments([...comments, { content: newComment, username }]);
            setNewComment('');
        } catch (error) {
            console.error('Error adding comment:', error.message);
        } finally {
            setSubmitting(false);
        }
    };

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>Error: {error}</div>;
    }

    return (
        <div>
            <h2>Post Details</h2>
            <p>Title: {post.title}</p>
            <p>Content: {post.content}</p>
            <p>User Name: {post.username}</p>
            <p>Upvotes: {post.upvotes}</p>
            {post.imageUrl && <img src={post.imageUrl} alt="Post Image" />}
            <p>Created At: {post.created_at}</p>
            <h3>Comments</h3>
            {comments.length > 0 ? (
                <ul>
                    {comments.map((comment, index) => (
                        <li key={index}>
                            <p>User: {comment.username}</p>
                            <p>Content: {comment.content}</p>
                        </li>
                    ))}
                </ul>
            ) : (
                <p>No comments available.</p>
            )}
            <form onSubmit={handleSubmitComment}>
                <input
                    type="text"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                    placeholder="Your Username"
                    disabled={submitting}
                />
                <textarea
                    value={newComment}
                    onChange={(e) => setNewComment(e.target.value)}
                    placeholder="Write your comment here..."
                    disabled={submitting}
                />
                <button type="submit" disabled={!newComment.trim() || submitting}>
                    {submitting ? 'Submitting...' : 'Submit Comment'}
                </button>
            </form>
            <div>
                <button onClick={handleEditPost}>Edit Post</button>
                <button onClick={handleDeletePost}>Delete Post</button>
            </div>
            <EditPost postId={postId} />
        </div>
    );
};

export default PostDetails;
