import React, { useState } from 'react';

const CreatePostForm = ({ onSubmit }) => {
    const [title, setTitle] = useState('');
    const [content, setContent] = useState('');
    const [imageUrl, setImageUrl] = useState('');
    const [username, setUsername] = useState(''); // Add state for username

    const handleSubmit = (e) => {
        e.preventDefault();
        // Create a new post object with the entered data
        const newPost = {
            title,
            content,
            imageUrl,
            username, // Include username in the post data
        };
        // Call the onSubmit function passed from the parent component with the new post data
        onSubmit(newPost);
        // Clear the form fields after submission
        setTitle('');
        setContent('');
        setImageUrl('');
        setUsername('');
    };

    return (
        <div>
            <h2>Create New Post</h2>
            <form onSubmit={handleSubmit}>
                <label>
                    Title:
                    <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} />
                </label>
                <label>
                    Content:
                    <textarea value={content} onChange={(e) => setContent(e.target.value)} />
                </label>
                <label>
                    Image URL:
                    <input type="text" value={imageUrl} onChange={(e) => setImageUrl(e.target.value)} />
                </label>
                {/* Add input field for username */}
                <label>
                    Username:
                    <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} />
                </label>
                <button type="submit">Submit</button>
            </form>
        </div>
    );
};

export default CreatePostForm;
