import React, { useState, useEffect } from 'react';
import './HomeFeed.css'; // Import the CSS file
import supabase from '../client'; // Assuming you have a Supabase client instance set up

const HomeFeed = ({ onPostSelected }) => {
    const [posts, setPosts] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        // Fetch posts data from Supabase when the component mounts
        const fetchPosts = async () => {
            try {
                const { data, error } = await supabase.from('Posts').select('*');
                if (error) {
                    throw error;
                }
                setPosts(data);
                setLoading(false);
            } catch (error) {
                console.error('Error fetching posts:', error.message);
            }
        };

        fetchPosts();
    }, []);

    if (loading) {
        return <div>Loading...</div>; // Display loading state while fetching data
    }

    // Function to handle clicking on a post
    const handlePostClick = (post) => {
        onPostSelected(post);
    };

    // Function to handle adding upvotes to a post
    const handleAddUpvote = async (postId, currentUpvotes) => {
        try {
            const { data: updatedPost, error } = await supabase
                .from('Posts')
                .update({ upvotes: currentUpvotes + 1 })
                .eq('id', postId)
                .single();
            if (error) {
                throw error;
            }
            const updatedPosts = posts.map(p => p.id === postId ? updatedPost : p);
            setPosts(updatedPosts);
        } catch (error) {
            console.error('Error adding upvote:', error.message);
        }
    };

    return (
        <div>
            <h2>Home Feed</h2>
            <ul>
                {posts.map((post) => (
                    <li key={post.id} className="post" onClick={() => handlePostClick(post)}>
                        <p>Title: {post.title}</p>
                        <p>Content: {post.content}</p>
                        <p>UserID: {post.username}</p>
                        <p>Upvotes: {post.upvotes}</p>
                        {post.imageUrl && <img src={post.imageUrl} alt="Post Image" />}
                        <p className="created-at">Created At: {post.created_at}</p>
                        <button onClick={(e) => {
                            e.stopPropagation();
                            handleAddUpvote(post.id, post.upvotes);
                        }}>Upvote</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default HomeFeed;
