// AboutPage.js
import React, { useEffect, useState } from 'react';
import supabase from '../client'; // Ensure this path is correct
import './AboutPage.css'; // Import the CSS file

const AboutPage = () => {
    const [mages, setMages] = useState([]);
    const [warriors, setWarriors] = useState([]);
    const [selectedCategory, setSelectedCategory] = useState('');

    useEffect(() => {
        const fetchCrewmates = async () => {
            // Fetch mages
            const { data: magesData, error: magesError } = await supabase.from('Posts').select('*').eq('category', 'mage');
            if (magesError) {
                console.error('Error fetching mages:', magesError.message);
            } else {
                setMages(magesData || []);
            }

            // Fetch warriors
            const { data: warriorsData, error: warriorsError } = await supabase.from('Posts').select('*').eq('category', 'warrior');
            if (warriorsError) {
                console.error('Error fetching warriors:', warriorsError.message);
            } else {
                setWarriors(warriorsData || []);
            }
        };

        fetchCrewmates();
    }, []);

    const handleCategoryChange = (event) => {
        setSelectedCategory(event.target.value);
    };

    const filteredCrewmates = selectedCategory === 'mage' ? mages : selectedCategory === 'warrior' ? warriors : [];

    return (
        <div>
            <h2>About Page</h2>
            <div>
                <label htmlFor="category">Select Category:</label>
                <select id="category" value={selectedCategory} onChange={handleCategoryChange}>
                    <option value="">Select</option>
                    <option value="mage">Mage</option>
                    <option value="warrior">Warrior</option>
                </select>
            </div>
            <div>
                <h3>Crewmates</h3>
                <ul>
                    {filteredCrewmates.map((crewmate) => (
                        <li key={crewmate.id}>
                            <h4 className="name">{crewmate.name}</h4>
                            <p>MPH: {crewmate.mph}</p>
                            <p className="color">Color: {crewmate.color}</p>
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
};

export default AboutPage;
