import React, { useState } from 'react';
import './App.css'; // Import app.css here
import HomeFeed from './pages/HomeFeed';
import PostDetails from './pages/PostDetails'; // Import PostDetails component
import CreatePostForm from './pages/CreatePostForm';
import EditPost from './pages/EditPost'; // Import EditPost component

const App = () => {
    const [selectedPost, setSelectedPost] = useState(null);
    const [page, setPage] = useState('home');

    // Function to handle selecting a post in HomeFeed
    const handlePostSelected = (post) => {
        setSelectedPost(post);
        setPage('post-details');
    };

    // Function to handle navigation to different pages
    const navigate = (pageName) => {
        setPage(pageName);
    };

    let content;
    switch (page) {
        case 'home':
            content = <HomeFeed onPostSelected={handlePostSelected} />;
            break;
        case 'new':
            content = <CreatePostForm />;
            break;
        case 'edit-post':
            content = <EditPost />;
            break;
        case 'post-details':
            content = <PostDetails post={selectedPost} />;
            break;
        default:
            content = <HomeFeed onPostSelected={handlePostSelected} />;
    }

    return (
        <div className="App">
            <div className="header">
                <h1>Let's hear what you think about NFL teams</h1>
                <button onClick={() => navigate('home')}>View Posts üåü</button>
                <button onClick={() => navigate('new')}>Add New Post üõ†Ô∏è</button>
                <a href="/about"><button>About ‚ÑπÔ∏è</button></a>
            </div>
            {content}
        </div>
    );
}

export default App;
