import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import supabase from './client';
import { Link } from 'react-router-dom';
import CreatePostForm from './pages/CreatePostForm';
import HomeFeed from './pages/HomeFeed';
import PostDetails from './pages/PostDetails';

const App = () => {
    const [posts, setPosts] = useState([]);

    useEffect(() => {
        async function fetchPosts() {
            try {
                const { data, error } = await supabase.from('Posts').select('*');
                if (error) {
                    throw error;
                }
                setPosts(data);
            } catch (error) {
                console.error('Error fetching posts:', error.message);
            }
        }
        fetchPosts();
    }, []);

    const handleSubmit = async (postData) => {
        try {
            const { data, error } = await supabase.from('Posts').insert([postData]);
            if (error) {
                throw error;
            }
            setPosts([...posts, data[0]]);
        } catch (error) {
            console.error('Error inserting new post:', error.message);
        }
    };

    return (
        <Router>
            <div className="App">
                <div className="header">
                    <h1>üöÄ Crewmates 1.0</h1>
                    <Link to="/"><button className="headerBtn"> View Posts üåü </button></Link>
                    <Link to="/new"><button className="headerBtn"> Add New Post üõ†Ô∏è </button></Link>
                    <Link to="/about"><button className="headerBtn"> About ‚ÑπÔ∏è </button></Link>
                </div>
                <Switch>
                    <Route exact path="/" render={(props) => <HomeFeed {...props} posts={posts} />} />
                    <Route path="/new" render={(props) => <CreatePostForm {...props} onSubmit={handleSubmit} />} />
                    <Route path="/post/:postId" component={PostDetails} />
                </Switch>
            </div>
        </Router>
    );
}

export default App;
