import React, { useState, useEffect } from 'react';
import './App.css'; // Import app.css here
import HomeFeed from './pages/HomeFeed';
import PostDetails from './pages/PostDetails'; // Import PostDetails component
import CreatePostForm from './pages/CreatePostForm';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom'; // Import BrowserRouter and Switch

const App = () => {
    return (
        <Router>
            <div className="App">
                <div className="header">
                    <h1>üöÄ Crewmates 1.0</h1>
                    <button><Link to="/">View Posts üåü</Link></button>
                    <button><Link to="/new">Add New Post üõ†Ô∏è</Link></button>
                    <button><Link to="/about">About ‚ÑπÔ∏è</Link></button>
                </div>
                <Switch>
                    <Route exact path="/" component={HomeFeed} />
                    <Route path="/new" component={CreatePostForm} />
                    <Route path="/post/:postId" component={PostDetails} />
                    <Route path="*" component={() => <div>404 Not Found</div>} />
                </Switch>
            </div>
        </Router>
    );
}

export default App;
