import React, { useState, useEffect } from 'react';
import './PostDetails.css'; // Import the CSS file
import  supabase  from '../client'; // Assuming you have a Supabase client instance set up

const PostDetails = ({ post }) => {
    const [comments, setComments] = useState([]);
    const [loading, setLoading] = useState(true);
    const [newComment, setNewComment] = useState('');
    const [submitting, setSubmitting] = useState(false);

    useEffect(() => {
        // Fetch comments data from the post object
        const fetchComments = async () => {
            try {
                // Extract comments from the post object
                const postComments = post.comments || [];
                setComments(postComments);
                setLoading(false);
            } catch (error) {
                console.error('Error fetching comments:', error.message);
            }
        };

        fetchComments();
    }, [post]); // Update comments when post object changes

    const handleSubmitComment = async (e) => {
        e.preventDefault();
        if (!newComment.trim()) return;
        
        setSubmitting(true);
        
        try {
            // Update the 'comments' column in the 'Posts' table with the new comment
            const { data, error } = await supabase
                .from('Posts')
                .update({ comments: [...comments, { content: newComment, username: 'Anonymous' }] }) // Assuming 'comments' is an array in your 'Posts' table
                .eq('id', post.id);

            if (error) {
                throw error;
            }

            // Update the local state with the new comment
            setComments([...comments, { content: newComment, username: 'Anonymous' }]);
            setNewComment('');
        } catch (error) {
            console.error('Error adding comment:', error.message);
        } finally {
            setSubmitting(false);
        }
    };

    if (loading) {
        return <div>Loading comments...</div>; // Display loading state while fetching data
    }

    return (
        <div>
            <h2>Post Details</h2>
            <p>Title: {post.title}</p>
            <p>Content: {post.content}</p>
            <p>User Name: {post.username}</p>
            <p>Upvotes: {post.upvotes}</p>
            {post.imageUrl && <img src={post.imageUrl} alt="Post Image" />}
            <p>Created At: {post.created_at}</p>
            <h3>Comments</h3>
            <ul>
                {comments.map((comment, index) => (
                    <li key={index}>
                        <p>User: {comment.username}</p>
                        <p>Content: {comment.content}</p>
                    </li>
                ))}
            </ul>
            <form onSubmit={handleSubmitComment}>
                <textarea
                    value={newComment}
                    onChange={(e) => setNewComment(e.target.value)}
                    placeholder="Write your comment here..."
                    disabled={submitting}
                />
                <button type="submit" disabled={!newComment.trim() || submitting}>
                    {submitting ? 'Submitting...' : 'Submit Comment'}
                </button>
            </form>
        </div>
    );
};

export default PostDetails;
