import React, { useState, useEffect } from 'react';
import './HomeFeed.css';
import supabase from '../client';

const HomeFeed = ({ onPostSelected }) => {
    const [posts, setPosts] = useState([]);
    const [loading, setLoading] = useState(true);
    const [searchTerm, setSearchTerm] = useState('');

    useEffect(() => {
        const fetchPosts = async () => {
            try {
                const { data, error } = await supabase.from('Posts').select('*');
                if (error) {
                    throw error;
                }
                setPosts(data);
                setLoading(false);
            } catch (error) {
                console.error('Error fetching posts:', error.message);
            }
        };

        fetchPosts();
    }, []);

    const handleUpvote = async (postId, post) => {
        try {
            const { data, error } = await supabase
                .from('Posts')
                .update({ upvotes: post.upvotes + 1 })
                .eq('id', postId);
            if (error) {
                throw error;
            }
            const updatedPosts = posts.map(p => {
                if (p.id === postId) {
                    return { ...p, upvotes: p.upvotes + 1 };
                }
                return p;
            });
            setPosts(updatedPosts);
        } catch (error) {
            console.error('Error adding upvote:', error.message);
        }
    };

    const handlePostClick = (post) => {
        onPostSelected(post);
    };

    const handleViewDetails = (post) => {
        onPostSelected(post);
    };

    const handleSearch = (event) => {
        setSearchTerm(event.target.value);
    };

    // Filter posts based on search term
    const filteredPosts = posts.filter(post =>
        post.title.toLowerCase().includes(searchTerm.toLowerCase())
    );

    if (loading) {
        return <div>Loading...</div>;
    }

    return (
        <div>
            <h2>Home Feed</h2>
            <input
                type="text"
                placeholder="Search by title"
                value={searchTerm}
                onChange={handleSearch}
            />
            <ul>
                {filteredPosts.map((post) => (
                    <li key={post.id} className="post">
                        <p>Title: {post.title}</p>
                        <p>Content: {post.content}</p>
                        <p>UserID: {post.username}</p>
                        <p>Upvotes: {post.upvotes}</p>
                        {post.imageUrl && <img src={post.imageUrl} alt="Post Image" />}
                        <p className="created-at">Created At: {new Date(post.created_at).toLocaleString()}</p>
                        <button onClick={() => handleUpvote(post.id, post)}>Upvote</button>
                        <button onClick={() => handleViewDetails(post)}>View Details</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default HomeFeed;
