// App.js
import './App.css';
import React, { useState, useEffect } from 'react';
import { useRoutes } from 'react-router-dom';
import ReadCrewmates from './pages/ReadCrewmate';
import CreateCrewmate from './pages/CreateCrewmate';
import EditCrewmate from './pages/EditCrewmate';
import AboutPage from './pages/AboutPage'; // Import your AboutPage component
import supabase from './client'; // Import your Supabase client
import { Link } from 'react-router-dom';

const App = () => {
    // Define state to hold crewmates data
    const [crewmates, setCrewmates] = useState([]);

    // Fetch crewmates data from Supabase on component mount
    useEffect(() => {
        async function fetchCrewmates() {
            try {
                const { data, error } = await supabase.from('Posts').select('*');
                if (error) {
                    throw error;
                }
                setCrewmates(data);
            } catch (error) {
                console.error('Error fetching crewmates:', error.message);
            }
        }
        fetchCrewmates();
    }, []);

    // Sets up routes
    let element = useRoutes([
        {
            path: "/",
            element: <ReadCrewmates data={crewmates} />
        },
        {
            path: "/edit/:id",
            element: <EditCrewmate data={crewmates} />
        },
        {
            path: "/new",
            element: <CreateCrewmate />
        },
        {
            path: "/about",
            element: <AboutPage />
        }
    ]);

    return (
        <div className="App">
            <div className="header">
                <h1>üöÄ Crewmates 1.0</h1>
                <Link to="/"><button className="headerBtn"> View Crewmates üåü </button></Link>
                <Link to="/new"><button className="headerBtn"> Add New Crewmate üõ†Ô∏è </button></Link>
                <Link to="/about"><button className="headerBtn"> About ‚ÑπÔ∏è </button></Link>
            </div>
            {element}
        </div>
    );
}

export default App;
