import React, { useState, useEffect } from 'react';
import './PostDetails.css'; // Import the CSS file
import supabase from '../client'; // Assuming you have a Supabase client instance set up

const PostDetails = ({ post }) => {
    const [comments, setComments] = useState(null);
    const [loading, setLoading] = useState(true);
    const [newComment, setNewComment] = useState('');
    const [submitting, setSubmitting] = useState(false);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchComments = async () => {
            try {
                // Fetch comments data from the server using Supabase
                const { data, error } = await supabase
                    .from('Comments')
                    .select('*')
                    .eq('post_id', post.id);

                if (error) {
                    throw new Error(error.message);
                }

                setComments(data || []); // Set comments to an empty array if there are no comments
                setLoading(false);
            } catch (error) {
                console.error('Error fetching comments:', error.message);
                setError('Error fetching comments. Please try again later.');
                setLoading(false);
            }
        };

        fetchComments();
    }, [post]); // Update comments when post object changes

    const handleSubmitComment = async (e) => {
        e.preventDefault();
        if (!newComment.trim()) return;
        
        setSubmitting(true);
        
        try {
            // Add the new comment to the database
            const { data, error } = await supabase.from('Comments').insert([
                { post_id: post.id, content: newComment, username: 'Anonymous' } // You can change 'Anonymous' to the actual username
            ]);

            if (error) {
                throw error;
            }

            // Update the local state with the new comment
            setComments([...comments, data[0]]);
            setNewComment('');
        } catch (error) {
            console.error('Error adding comment:', error.message);
        } finally {
            setSubmitting(false);
        }
    };

    if (loading) {
        return <div>Loading comments...</div>; // Display loading state while fetching data
    }

    if (error) {
        return <div>Error: {error}</div>; // Display error message if there's an issue with fetching comments
    }

    return (
        <div>
            <h2>Post Details</h2>
            <p>Title: {post.title}</p>
            <p>Content: {post.content}</p>
            <p>User Name: {post.username}</p>
            <p>Upvotes: {post.upvotes}</p>
            {post.imageUrl && <img src={post.imageUrl} alt="Post Image" />}
            <p>Created At: {post.created_at}</p>
            <h3>Comments</h3>
            {Array.isArray(comments) && comments.length > 0 ? (
                <ul>
                    {comments.map((comment, index) => (
                        <li key={index}>
                            <p>User: {comment.username}</p>
                            <p>Content: {comment.content}</p>
                        </li>
                    ))}
                </ul>
            ) : (
                <p>No comments available.</p>
            )}
            <form onSubmit={handleSubmitComment}>
                <textarea
                    value={newComment}
                    onChange={(e) => setNewComment(e.target.value)}
                    placeholder="Write your comment here..."
                    disabled={submitting}
                />
                <button type="submit" disabled={!newComment.trim() || submitting}>
                    {submitting ? 'Submitting...' : 'Submit Comment'}
                </button>
            </form>
        </div>
    );
};

export default PostDetails;
